<div class="list-group" ng-if="categories">	<div class="c-menu">		<ul>			<li class="list-group-item" ng-repeat="category in categories">				<a href="<?php echo base_url(); ?>products/{{category.id}}">{{ category.name }} <span class="glyphicon glyphicon-chevron-right pull-right"></span></a>				<div ng-if="category.children" class="c-submenu-wrap">					<div class="c-submenu-styler">						<div class="c-submenu-col category-list" ng-repeat="subcategory in category.children">							<div class="pad">								<h3 class="category-list-title font-semibold">									<a href="<?php echo base_url(); ?>products/{{category.id}}/{{subcategory.id}}">{{ subcategory.name }}</a>								</h3>								<ul ng-if="subcategory.children">									<li ng-repeat="lastcategory in subcategory.children">										<a href="<?php echo base_url(); ?>products/{{category.id}}/{{subcategory.id}}/{{lastcategory.id}}">{{ lastcategory.name }}</a>									</li>								</ul>							</div>						</div>					</div>				</div>			</li>		</ul>	</div></div>