<!-- Menu================================================== --><div class="main-menu">	<!--button type="button" class="btn btn-default"><span class="glyphicon glyphicon-home" aria-hidden="true"></span> Home</button-->	<div class="btn-group" role="group">		<button type="button" class="btn btn-default dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">			<span class="glyphicon glyphicon-list" aria-hidden="true"></span> Categories			<span class="caret"></span>		</button>				<ul class="dropdown-menu" ng-if="categories">			<li ng-class="{	'dropdown-submenu':category.subcategories }" ng-repeat="category in categories">				<a href="products/{{category.id}}">{{ category.name }}</a>				<ul ng-if="category.subcategories" class="dropdown-menu">					<li ng-repeat="subcategory in category.subcategories">						<a href="products/{{category.id}}/{{subcategory.id}}">{{ subcategory.name }}</a>					</li>				</ul>			</li>		</ul>	</div></div><!-- /.Menu -->